object CarrierData {

    // Maps the text content of a PDF cell according to a Yellow/Green rule interpretation
    // from the "Billable Services 2024 PDF".
    // Yellow cells/Text = Service available (value is the text/Yes).
    // Green cells / Blank cells / "N" = Service unavailable (value is null).
    private fun mapCellValue(cellText: String?): String? {
        val trimmedText = cellText?.trim()
        return when {
            trimmedText.isNullOrBlank() -> null
            trimmedText.equals("N", ignoreCase = true) -> null
            trimmedText.equals("Y", ignoreCase = true) -> "Yes"
            else -> trimmedText
        }
    }

